import axios from 'axios';

// הגדרת כתובת ה-API כברירת מחדל
const apiUrl = "https://localhost:5144"; 
axios.defaults.baseURL = apiUrl;  // הגדרה לכל הקריאות שמבצעות את הקריאות ל-API

// הוספת interceptor לתפיסת שגיאות
axios.interceptors.response.use(
  response => response,  // אם אין שגיאה, מחזירים את התשובה כמו שהיא
  error => {
    console.error("Error in API call:", error);  // רישום השגיאה בלוג
    return Promise.reject(error);  // החזרת השגיאה הלאה
  }
);

export default {
  // שליפת כל המשימות
  getTasks: async () => {
    try {
      const result = await axios.get('/tasks');  // הפניה ל-API לשליפת המשימות
      return result.data;
    } catch (error) {
      console.error("Error fetching tasks:", error);
      return [];
    }
  },

  // הוספת משימה חדשה
  addTask: async (name) => {
    try {
      const result = await axios.post('/tasks', { name, isComplete: false });
      return result.data;
    } catch (error) {
      console.error("Error adding task:", error);
      return {};
    }
  },

  // עדכון משימה (הגדרת "הושלמה")
  setCompleted: async (id, isComplete) => {
    try {
      const result = await axios.put(`/tasks/${id}`, { isComplete });
      return result.data;
    } catch (error) {
      console.error("Error updating task:", error);
      return {};
    }
  },

  // מחיקת משימה
  deleteTask: async (id) => {
    try {
      await axios.delete(`/tasks/${id}`);
      return { success: true };
    } catch (error) {
      console.error("Error deleting task:", error);
      return { success: false };
    }
  }
};
